// src/components/StudentCard.js
import React from "react";
import "./App.css";

const StudentCard = ({ student }) => {
    const { imageSrc, imageName, firstName, lastName, rollNumber, graduationYear, domainName } = student;

    return (
      <div className="student-card">
        <div className="student-image-container">
          {imageSrc && (
            <img src={imageSrc} alt={imageName || "Student Image"} className="student-image" />
          )}
          {!imageSrc && (
            <div className="student-image-placeholder">
              <i className="fas fa-user" /> {/* Font Awesome icon for placeholder */}
            </div>
          )}
        </div>
        <div className="student-info">
          <h3>{`${firstName} ${lastName}`}</h3>
          <p>
            <strong>Roll Number:</strong> {rollNumber}
          </p>
          <p>
            <strong>Graduation Year:</strong> {graduationYear}
          </p>
          <p>
            <strong>Domain:</strong> {domainName}
          </p>
        </div>
        <div className="view-btn">View More</div> {/* Optional: Add hover effect or interaction */}
      </div>
    );
  };

  export default StudentCard;
