import React, { useEffect, useState } from "react";
import { getStudents } from "../api/api";
import StudentCard from "../components/StudentCard";
import "../components/App.css";

const Home = () => {
    const [students, setStudents] = useState([]); // Initialize as an empty array
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchStudents = async () => {
            try {
                const data = await getStudents();
                co
                setStudents(data); // Set students data on successful fetch
            } catch (err) {
                console.error(err); // Log error to the console for debugging
                setError("Failed to load student data. Please try again later.");
            }
        };
        fetchStudents();
    }, []);

    return (
        <div className="student-card-section">
            <h2>Student Details</h2>
            {error && <p className="error-message">{error}</p>}
            <div className="student-cards">
                {students.length > 0 ? (
                    students.map((student) => (
                        <StudentCard key={student.rollNumber} student={student} />
                    ))
                ) : (
                    !error && <p>Loading students...</p> // Show a loading message if no error
                )}
            </div>
        </div>
    );
};

export default Home;



